<style lang="scss" scoped>
.thanks {
	position: fixed;
	z-index: 999;
	background: rgba(14,14,17,.7);
	top: 0; left: 0;
	width: calc(100vw - 32px);
	min-height: calc(100vh - 32px);
	overflow-y: auto;
	padding: 16px;
	@media (min-width: 992px) {
		padding: 50px;
		width: calc(100vw - 100px);
		min-height: calc(100vh - 200px);
	}
	&--inner {
		background: var(--whitish);
		border-radius: 12px;
		min-height: calc(100vh - 200px);
		max-width: 1340px;
		margin: 0 auto;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		position: relative;
		padding: 85px 16px 32px;
	}
	&--bg-img {
		position: relative;
		.pic-desktop {
			display: none;
			@media(min-width:768px) {
				display: block;
  		}
		}
		.pic-mobile {
			display: block;
			@media(min-width:768px) {
				display: none;
  		}
		}
	}
	&--btn-desk {
		display: none;
		position: absolute;
		right: 50%;
		bottom: 14px;
		@media (min-width: 992px) {
			display: flex;
		}
		@media (min-width: 1440px) {
			bottom: 20px;
		}
	}
	&--btn-mob {
		display: flex;
		margin-right: auto;
		margin-top: 32px;
		@media (min-width: 992px) {
			display: none;
		}
	}
	&--close {
		svg {
			position: absolute;
			top: 24px;
			right: 24px;
		}
	}
	.read-more {
		svg {
			margin-right: 4px;
		}
	}
}
</style>